import React, { useState } from 'react';
import {Button, FormControl, FormGroup, FormLabel, MenuItem, Paper, Select, TextField } from '@mui/material';
function PokemonAdd({addPokemon, pokemonTypes}) {
    const [name, setName] = useState('');
    const [description, setDescription] = useState('');
    const [type, setType] = useState(1);

    const handleSubmit = (e) => {
        e.preventDefault();
        let pokemon = {
            name:name,
            description:description,
            type:type,
        };
        addPokemon(pokemon);
    }
  
    return (
    //Render first the load this... probably put this in the beginning?
    <Paper>
        <FormGroup>
        <form onSubmit={(e) => {handleSubmit(e)}}>
        <FormLabel>Name</FormLabel>
            <TextField onChange={e =>{setName(e.target.value)}} id="name"/>
            <FormLabel>Description</FormLabel>
            <TextField onChange={e => {setDescription(e.target.value)}} id="description"/>
            <Select onChange={e => {setType(e.target.value)}} defaultValue={1}>
                {pokemonTypes.map(type => {
                    return(
                        <MenuItem key={type.PokemonTypePK}  value={type.PokemonTypePK}>{type.Description}</MenuItem>
                    );
                })}
            </Select>
           </form>
           <Button type="submit" onClick={console.log(`${name}, ${description}, ${type}`)}>Add Pokemon</Button>
        </FormGroup>
       
    </Paper>
    )
}

export default PokemonAdd;
