import React, { useState } from 'react';
import {Button, FormControl, FormLabel, MenuItem, Paper, Select, TextField } from '@mui/material';
function PokemonAdd({addPokemon, pokemonTypes}) {
    const [name, setName] = useState('');
    const [description, setDescription] = useState('');
    const [type, setType] = useState(1);

  
    return (
    //Render first the load this... probably put this in the begginning?
    <Paper>
    <FormControl>
    <FormLabel>Name</FormLabel>
    <TextField onChange={e => setName(e.target.value)} id="name"/>
    <FormLabel>Description</FormLabel>
    <TextField onChange={e => setDescription(e.target.value)} id="description"/>
    <Select onChange={e => setType(e.target.value)} defaultValue={1}>
        {pokemonTypes.map(type => {
            return(
                <MenuItem key={type.PokemonTypePK}  value={type.PokemonTypePK}>{type.Description}</MenuItem>
            );
        })}
    </Select>
    <Button onClick={addPokemon(name, description, type)}>Add Pokemon</Button>
    </FormControl>
    </Paper>
    )
}

export default PokemonAdd;
